mapper.select:
SELECT schedule.schedule_code, schedule.admin_code, schedule.schedule_date, schedule.schedule_name
FROM schedule,
     admin
Where schedule_date = #{schedule_date}
  AND admin.company_code = #{company_code}

mapper.update:
UPDATE schedule
SET admin_code=#{admin_code},
    schedule_date=#{schedule_date},
    schedule_name=#{schedule_name}
WHERE schedule_code = #{schedule_code}
